import { Link, useParams, useSearchParams } from 'react-router-dom';
import { useState, useEffect} from 'react';
import ProductList from '../product-list/product-list';
import Pagination from '../pagination/pagination';

import { getCatalog } from '../../store/guitars-data/selector';
import { useAppSelector } from '../../hooks';
import { SortType, SortOrder, GUITAR_COUNT_SHOWN } from '../../consts';

function Catalog(): JSX.Element {
  const guitarsCatalog = useAppSelector(getCatalog);

  const [searchParams, setSearchParams] = useSearchParams();

  const [guitarsCatalogSorted, setGuitarsCatalogSorted] = useState(guitarsCatalog);
  const [sortTypeChecked, setSortTypeChecked] = useState(SortType.Original);
  const [sortOrderChecked, setSortOrderChecked] = useState(SortOrder.Original);

  const {pageNumber = 1} = useParams<{pageNumber: string}>();
  const pagesCount = Math.ceil(guitarsCatalog.length/GUITAR_COUNT_SHOWN);

  function handleSearchParamsChange(key: string,  value: string) {
    searchParams.set(key, value);
    setSearchParams(searchParams);
  }

  const getChangeSortTypeChecked = () => {
    if (sortTypeChecked===SortType.Original) {
      setSortTypeChecked(SortType.Price);
    }
  };

  useEffect(() => {
    if (sortTypeChecked !== SortType.Popularity) {
      if (sortOrderChecked === SortOrder.Up) {
        return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => prev.price - next.price));
      }

      if ( sortOrderChecked === SortOrder.Down) {
        return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => next.price - prev.price));
      }
    } else {
      if (sortOrderChecked === SortOrder.Up) {
        return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => prev.rating - next.rating));
      }

      if ( sortOrderChecked === SortOrder.Down) {
        return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => next.rating - prev.rating));
      }
    }
    return setGuitarsCatalogSorted(guitarsCatalog.slice(0));
  }, [guitarsCatalog, sortTypeChecked, sortOrderChecked]);

  const guitarsForRender = guitarsCatalogSorted.slice(GUITAR_COUNT_SHOWN*(Number(pageNumber)-1),
    GUITAR_COUNT_SHOWN*(Number(pageNumber)-1)+GUITAR_COUNT_SHOWN);

  return (
    <div className="catalog" data-testid="catalog">
      <form className="catalog-filter">
        <h2 className="title title--bigger catalog-filter__title">Фильтр</h2>
        <fieldset className="catalog-filter__block">
          <legend className="catalog-filter__block-title">Цена, ₽</legend>
          <div className="catalog-filter__price-range">
            <div className="form-input">
              <label className="visually-hidden">Минимальная цена</label>
              <input type="number" placeholder="1 000" id="priceMin" name="от"/>
            </div>
            <div className="form-input">
              <label className="visually-hidden">Максимальная цена</label>
              <input type="number" placeholder="30 000" id="priceMax" name="до"/>
            </div>
          </div>
        </fieldset>
        <fieldset className="catalog-filter__block">
          <legend className="catalog-filter__block-title">Тип гитар</legend>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="acoustic" name="acoustic"/>
            <label htmlFor="acoustic">Акустические гитары</label>
          </div>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="electric" name="electric" defaultChecked/>
            <label htmlFor="electric">Электрогитары</label>
          </div>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="ukulele" name="ukulele" defaultChecked/>
            <label htmlFor="ukulele">Укулеле</label>
          </div>
        </fieldset>
        <fieldset className="catalog-filter__block">
          <legend className="catalog-filter__block-title">Количество струн</legend>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="4-strings" name="4-strings" defaultChecked/>
            <label htmlFor="4-strings">4</label>
          </div>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="6-strings" name="6-strings" defaultChecked/>
            <label htmlFor="6-strings">6</label>
          </div>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="7-strings" name="7-strings"/>
            <label htmlFor="7-strings">7</label>
          </div>
          <div className="form-checkbox catalog-filter__block-item">
            <input className="visually-hidden" type="checkbox" id="12-strings" name="12-strings" disabled/>
            <label htmlFor="12-strings">12</label>
          </div>
        </fieldset>
        <button className="catalog-filter__reset-btn button button--black-border button--medium" type="reset">Очистить</button>
      </form>
      <div className="catalog-sort">
        <h2 className="catalog-sort__title">Сортировать:</h2>
        <div className="catalog-sort__type">
          <Link className={`catalog-sort__type-button 
            ${sortTypeChecked===SortType.Price ? 'catalog-sort__type-button--active' : ''}`}
          aria-label="по цене"
          onClick={()=> {
            setSortTypeChecked(SortType.Price);
          }}
          to={`#sort_by_${sortTypeChecked.toLowerCase()}_${sortOrderChecked.toLowerCase()}`}
          // to={`#characteristics'}
          >по цене
          </Link>
          <button className={`catalog-sort__type-button
          ${sortTypeChecked===SortType.Popularity ? 'catalog-sort__type-button--active' : ''}`}
          aria-label="по популярности"
          onClick={()=> {
            setSortTypeChecked(SortType.Popularity);
          }}
          >по популярности
          </button>
        </div>
        <div className="catalog-sort__order">
          <button className={`catalog-sort__order-button catalog-sort__order-button--up
          ${sortOrderChecked===SortOrder.Up ? 'catalog-sort__order-button--active' : ''}`}
          aria-label="По возрастанию"
          onClick={()=> {
            setSortOrderChecked(SortOrder.Up);
            getChangeSortTypeChecked();
            handleSearchParamsChange('_sort', 'price');
            // window.location.assign({qqq});
            // window.location.assign(`#sort_by_${sortTypeChecked.toLowerCase()}_${sortOrderChecked.toLowerCase()}`);
          }}
          >
          </button>
          <button className={`catalog-sort__order-button catalog-sort__order-button--down
          ${sortOrderChecked===SortOrder.Down ? 'catalog-sort__order-button--active' : ''}`}
          aria-label="По убыванию"
          onClick={()=> {
            setSortOrderChecked(SortOrder.Down);
            getChangeSortTypeChecked();
          }}
          >
          </button>
        </div>
      </div>

      <ProductList catalog={guitarsForRender} />
      <Pagination pagesCount={pagesCount} pageCurrent={Number(pageNumber)} />
    </div>
  );
}

export default Catalog;
