import { createSlice } from '@reduxjs/toolkit';
import { NameSpace } from '../../consts';
import { QuestsProcess } from '../../types/state';

const initialState: QuestsProcess = {
  pageCurrent: '1',
  priceLimits: {
    priceMin: 0,
    priceMax: 0,
  },
};

export const guitarsProcess = createSlice({
  name: NameSpace.Process,
  initialState,
  reducers: {
    choosePage: (state, action) => {
      state.pageCurrent = action.payload;
    },
    updateMinPrice: (state, action) => {
      state.priceLimits.priceMin = action.payload;
      // eslint-disable-next-line no-console
      console.log('state.priceLimits.min');
      // eslint-disable-next-line no-console
      console.log(state.priceLimits.priceMin);
    },
    updateMaxPrice: (state, action) => {
      state.priceLimits.priceMax = action.payload;
      // eslint-disable-next-line no-console
      console.log('state.priceLimits.max');
      // eslint-disable-next-line no-console
      console.log(state.priceLimits.priceMax);
    },
  },
});

export const { choosePage, updateMaxPrice } = guitarsProcess.actions;
