// import {useState} from 'react';
// import {QuestionGenre, UserGenreQuestionAnswer} from '../types/question';
import { SortType, SortOrder } from '../consts';
import { Guitars } from '../types/guitars';


export const useSortType = (guitars:Guitars, sortTypeChecked:SortType, sortOrderChecked: SortOrder): Guitars => {
//   const sortByPriceUp = ()=> guitarsCatalog.slice(0).sort((prev, next) => prev.price - next.price);

  if (sortTypeChecked !== SortType.Popularity && sortOrderChecked === SortOrder.Up) {
    //   eslint-disable-next-line no-console
    console.log('сортирую по цене от меньшего  к большему');
    return guitars.slice(0).sort((prev, next) => prev.price - next.price);
  }
  return guitars;
};
