import { useState, useEffect, ChangeEvent } from 'react';
import { useAppDispatch } from '../../hooks';
import { getCatalog, getMinPrice, getMaxPrice } from '../../store/guitars-data/selector';

import { updateFilterPriceLimit, updateFilterTypes, updateFilterStringsCount } from '../../store/guitars-process/guitars-process';

import { useAppSelector } from '../../hooks';
import Preloader from '../preloader/preloader';

const guitarStrings :{[key: string]: string[]} = {
  'acoustic': ['6-strings', '7-strings', '12-strings'],
  'electric': ['4-strings', '6-strings', '7-strings'],
  'ukulele': ['4-strings'],
};

function Filter():JSX.Element {
  const dispatch = useAppDispatch();

  const guitarsCatalog = useAppSelector(getCatalog);
  const guitarsMinPrice = useAppSelector(getMinPrice);
  const guitarsMaxPrice = useAppSelector(getMaxPrice);

  const [filterPrice, setFilterPrice] = useState({
    priceMin: 0,
    priceMax: 0,
  });

  const [filterType, setFilterType] = useState<{[key: string]: boolean} >({
    acoustic: false,
    electric: false,
    ukulele: false,
  });

  const [filterStringsCount, setFilterStringsCount] = useState({
    '4-strings': false,
    '6-strings': false,
    '7-strings': false,
    '12-strings': false,
  });

  const [filterStringsCountChecked, setFilterStringsCountChecked] = useState({
    '4-strings': false,
    '6-strings': false,
    '7-strings': false,
    '12-strings': false,
  });

  useEffect(() => {
    const filterStringsItems = [''];

    Object.keys(filterType).forEach((el)=>{
      if ( filterType[el] ) {
        guitarStrings[el].forEach((item) => {
          if (!filterStringsItems.includes(item)) {
            filterStringsItems.push(item);
          }
        });
      }
    });

    setFilterStringsCount({
      '4-strings': filterStringsItems.length > 1 ? !filterStringsItems.includes('4-strings') : false,
      '6-strings': filterStringsItems.length > 1 ? !filterStringsItems.includes('6-strings'): false,
      '7-strings': filterStringsItems.length > 1 ? !filterStringsItems.includes('7-strings'): false,
      '12-strings': filterStringsItems.length > 1 ? !filterStringsItems.includes('12-strings'): false,
    });

    dispatch(updateFilterPriceLimit(filterPrice));
    dispatch(updateFilterTypes(filterType));
    dispatch(updateFilterStringsCount(filterStringsCountChecked));

  }, [dispatch, filterPrice, filterType, filterStringsCountChecked]);

  // useEffect(() => {
  //   const filterStringsUnchecked = [''];

  //   Object.keys(filterType).forEach((el)=>{
  //     if ( !filterType[el] ) {
  //       guitarStrings[el].forEach((item) => {
  //         if (!filterStringsUnchecked.includes(item)) {
  //           filterStringsUnchecked.push(item);
  //         }
  //       });
  //     }
  //   });

  //   setFilterStringsCountChecked({
  //     '4-strings': filterStringsUnchecked.length > 1 ? !filterStringsUnchecked.includes('4-strings'): filterStringsCount['4-strings'],
  //     '6-strings': filterStringsUnchecked.length > 1 ? !filterStringsUnchecked.includes('6-strings'): filterStringsCount['6-strings'],
  //     '7-strings': filterStringsUnchecked.length > 1 ? !filterStringsUnchecked.includes('7-strings'): filterStringsCount['7-strings'],
  //     '12-strings': filterStringsUnchecked.length > 1 ? !filterStringsUnchecked.includes('12-strings'): filterStringsCount['12-strings'],
  //   });
  // }, [filterType, filterStringsCount]);

  if (guitarsCatalog.length === 0) {
    return <Preloader />;
  }

  const fieldPriceChangeHandle = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;

    setFilterPrice({...filterPrice, [id]: value});
  };

  const handleMinPriceBlur = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;

    if ( value !== '' ) {
      if ( Number(value) < guitarsMinPrice ) {
        return setFilterPrice({...filterPrice, [id]: guitarsMinPrice});
      }

      if ( Number(value) > guitarsMaxPrice ) {
        return setFilterPrice({...filterPrice, [id]: guitarsMaxPrice});
      }

      if ( Number(value) > Number(filterPrice.priceMax) && filterPrice.priceMax !== 0) {
        return setFilterPrice({...filterPrice, [id]: filterPrice.priceMax});
      }
      return setFilterPrice({...filterPrice, [id]: value});
    } else {
      return setFilterPrice({...filterPrice, [id]: 0});
    }
  };

  const handleMaxPriceBlur = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;

    if ( value !== '' ) {
      if ( Number(value) < guitarsMinPrice ) {
        return setFilterPrice({...filterPrice, [id]: guitarsMinPrice});
      }

      if ( Number(value) > guitarsMaxPrice ) {
        return setFilterPrice({...filterPrice, [id]: guitarsMaxPrice});
      }

      if ( Number(value) < Number(filterPrice.priceMin) && filterPrice.priceMin !== 0) {
        return setFilterPrice({...filterPrice, [id]: filterPrice.priceMin});
      }
      return setFilterPrice({...filterPrice, [id]: value});
    } else {
      return setFilterPrice({...filterPrice, [id]: 0});
    }
  };

  const radioChangeHandler = (evt: ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = evt.target;
    setFilterType({...filterType, [name]: checked});
  };

  const onChangeStringsCount = (evt: ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = evt.target;
    setFilterStringsCountChecked({...filterStringsCountChecked, [name]: checked});
  };

  const onResetClick = () => {
    setFilterPrice({
      priceMin: guitarsMinPrice,
      priceMax: guitarsMaxPrice,
    });

    setFilterType({
      acoustic: false,
      electric: false,
      ukulele: false,
    });

    setFilterStringsCount({
      '4-strings': false,
      '6-strings': false,
      '7-strings': false,
      '12-strings': false,
    });
  };

  return (
    <form className="catalog-filter">
      <h2 className="title title--bigger catalog-filter__title">Фильтр</h2>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Цена, ₽</legend>
        <div className="catalog-filter__price-range">
          <div className="form-input">
            <label className="visually-hidden">Минимальная цена</label>
            <input type="number"  id="priceMin" name="от"
              placeholder={guitarsMinPrice.toLocaleString()}
              value={filterPrice.priceMin===0 ? '' : filterPrice.priceMin}
              onChange={fieldPriceChangeHandle}
              onBlur={handleMinPriceBlur}
            />
          </div>
          <div className="form-input">
            <label className="visually-hidden">Максимальная цена</label>
            <input type="number"   id="priceMax" name="до"
              placeholder={guitarsMaxPrice.toLocaleString()}
              value={filterPrice.priceMax===0 ? '' : filterPrice.priceMax}
              onChange={fieldPriceChangeHandle}
              onBlur={handleMaxPriceBlur}
            />
          </div>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Тип гитар</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="acoustic" name="acoustic"
            onChange={radioChangeHandler}
          />
          <label htmlFor="acoustic">Акустические гитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="electric" name="electric"
            onChange={radioChangeHandler}
          />
          <label htmlFor="electric">Электрогитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="ukulele" name="ukulele"
            onChange={radioChangeHandler}
          />
          <label htmlFor="ukulele">Укулеле</label>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Количество струн</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="4-strings" name="4-strings"
            checked={filterStringsCountChecked['4-strings']}
            disabled={filterStringsCount['4-strings']}
            onChange={onChangeStringsCount}
          />
          <label htmlFor="4-strings">4</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="6-strings" name="6-strings"
            checked={filterStringsCountChecked['6-strings']}
            disabled={filterStringsCount['6-strings']}
            onChange={onChangeStringsCount}
          />
          <label htmlFor="6-strings">6</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="7-strings" name="7-strings"
            checked={filterStringsCountChecked['7-strings']}
            disabled={filterStringsCount['7-strings']}
            onChange={onChangeStringsCount}
          />
          <label htmlFor="7-strings">7</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="12-strings" name="12-strings"
            checked={filterStringsCountChecked['12-strings']}
            disabled={filterStringsCount['12-strings']}
            onChange={onChangeStringsCount}
          />
          <label htmlFor="12-strings">12</label>
        </div>
      </fieldset>
      <button className="catalog-filter__reset-btn button button--black-border button--medium" type="reset"
        onClick={onResetClick}
      >Очистить
      </button>
    </form>
  );
}

export default Filter;
