import { Fragment } from 'react'; // useEffect, useRef, useState
import { useAppSelector } from '../../hooks';
import { STAR_COUNT, ratingRange, NameSpace } from '../../consts';

type Props = {
  id: number,
  width: string,
  height: string,
  rating: number,
  isReview: boolean,
}

function RatingBlock({id, rating, width, height, isReview}: Props): JSX.Element {

  const guitarsReviews = useAppSelector((state) => state[NameSpace.Data].guitarsComments[id]);

  return (
    <>
      {[...Array(STAR_COUNT)].map((_,index) => {
        const keyValue = index;
        return (
          <Fragment key={keyValue}>
            <svg width={width} height={height} aria-hidden="true"  data-testid="star">
              <use xlinkHref={Math.floor(rating)>index ? '#icon-full-star' : '#icon-star'}></use>
            </svg>
          </Fragment>
        );
      })}
      <p className="visually-hidden">Оценка: {ratingRange[Math.floor(rating)-1]}</p>
      <p className="rate__count">

        {isReview===false ?
          <Fragment>
            <span className="visually-hidden">Всего оценок:</span>{guitarsReviews===undefined ? '' : guitarsReviews.length}
            {/* <span className="visually-hidden">Всего оценок:</span> {commentsCount} */}
          </Fragment>
          :''}
      </p>
    </>
  );
}

export default RatingBlock;
