import { createSlice } from '@reduxjs/toolkit';
import { NameSpace } from '../../consts';
import { QuestsProcess } from '../../types/state';

const initialState: QuestsProcess = {
  pageCurrent: '1',
  priceLimits: {
    min: 0,
    max: 0,
  }
};

export const guitarsProcess = createSlice({
  name: NameSpace.Process,
  initialState,
  reducers: {
    choosePage: (state, action) => {
      state.pageCurrent = action.payload;
    },
    updateMinPrice: (state, action) => {
      state.priceLimits.min = action.payload;
    },
    updateMaxPrice: (state, action) => {
      state.priceLimits.max = action.payload;
    },
  },
});

export const { choosePage, chooseMinPrice } = guitarsProcess.actions;
