import { useEffect } from 'react';
import FocusLock from 'react-focus-lock';
import { isEscEvent } from '../../utils/utils';

type Props = {
  closeModalSuccessSubmit: ()=> void,
};

function ModalSuccess({closeModalSuccessSubmit}: Props): JSX.Element {

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    document.addEventListener('keydown', onBlockEscKeydown);
    return function () {
      document.body.style.overflow = 'visible';
      document.removeEventListener('keydown', onBlockEscKeydown);
    };
  });

  const onBlockEscKeydown = function (evt: KeyboardEvent) {
    if (isEscEvent(evt)) {
      evt.preventDefault();
      closeModalSuccessSubmit();
    }
  };

  return (
    <FocusLock>
      <div style={{position: 'relative', width: '550px', height: '410px', marginBottom: '50px'}}>
        <div className="modal is-active modal--success modal-for-ui-kit">
          <div className="modal__wrapper">
            <div className="modal__overlay" data-close-modal onClick={() => closeModalSuccessSubmit()}></div>
            <div className="modal__content">
              <svg className="modal__icon" width="26" height="20" aria-hidden="true">
                <use xlinkHref="#icon-success"></use>
              </svg>
              <p className="modal__message">Спасибо за ваш отзыв!</p>
              <div className="modal__button-container modal__button-container--review">
                <button className="button button--small modal__button modal__button--review"
                  onClick={() => closeModalSuccessSubmit()}
                  data-testid="button"
                  autoFocus
                >К покупкам!
                </button>
              </div>
              <button className="modal__close-btn button-cross" type="button" aria-label="Закрыть"
                onClick={() => closeModalSuccessSubmit()}
                data-testid="button"
              >
                <span className="button-cross__icon"></span>
                <span className="modal__close-btn-interactive-area"></span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </FocusLock>
  );
}

export default ModalSuccess;
