import { useState, ChangeEvent } from 'react';
import { useAppSelector } from '../../hooks';
import { getCatalog } from '../../store/guitars-data/selector';
import { Guitars } from '../../types/guitars';

function FormSearch (): JSX.Element {
  const guitarsCatalog = useAppSelector(getCatalog);

  const [isHideSelectItem, setIsHideSelectItem] = useState(true);
  const [formSearch, setFormSearch] = useState('');


  const fieldChangeHandle = function(evt:ChangeEvent<HTMLInputElement>) {
    const searchData = evt.target.value;
    setFormSearch(searchData);
    if (searchData.length!==0) {
      setIsHideSelectItem(false);
    } else {
      setIsHideSelectItem(true);
    }

    const matches = [] as Guitars;
    guitarsCatalog.forEach((elem)=> {
      if (elem.name.toLowerCase().indexOf(searchData.toLowerCase()) !== -1) {
        matches.push(elem);
      }
    });

    // eslint-disable-next-line no-console
    console.log(matches);
  };

  return (
    <div className="form-search">
      <form className="form-search__form" id="form-search">
        <button className="form-search__submit" type="submit">
          <svg className="form-search__icon" width="14" height="15" aria-hidden="true">
            <use xlinkHref="#icon-search"></use>
          </svg><span className="visually-hidden">Начать поиск</span>
        </button>
        <input className="form-search__input" id="search" type="text" autoComplete="off" placeholder="что вы ищите?"
          value={formSearch}
          onChange={fieldChangeHandle}
        />
        <label className="visually-hidden" htmlFor="search">Поиск</label>
      </form>
      <ul className={`form-search__select-list ${isHideSelectItem===true ? 'hidden' : ''}`}>
        <li className="form-search__select-item" tabIndex={0}>Четстер Plus</li>
        <li className="form-search__select-item" tabIndex={0}>Четстер UX</li>
        <li className="form-search__select-item" tabIndex={0}>Четстер UX2</li>
        <li className="form-search__select-item" tabIndex={0}>Четстер UX3</li>
        <li className="form-search__select-item" tabIndex={0}>Четстер UX4</li>
        <li className="form-search__select-item" tabIndex={0}>Четстер UX5</li>
      </ul>
      <button className="form-search__reset" type="reset" form="form-search">
        <svg className="form-search__icon" width="14" height="15" aria-hidden="true">
          <use xlinkHref="#icon-close"></use>
        </svg><span className="visually-hidden">Сбросить поиск</span>
      </button>
    </div>
  );
}

export default FormSearch;
