import { useParams } from 'react-router-dom'; //useSearchParams
import { useEffect } from 'react';//useState,
import { store } from '../../store/index';
import { fetchCatalogGuitars } from '../../store/api-actions';

import Filter from '../filter/filter';
import Sort from '../sort/sort';
import ProductList from '../product-list/product-list';
import Pagination from '../pagination/pagination';

import { getCatalog } from '../../store/guitars-data/selector';
import { getPriceLimits, getFilterTypes, getFilter4Strings, getFilter6Strings, getFilter7Strings, getFilter12Strings } from '../../store/guitars-process/selector';
import { useAppSelector } from '../../hooks';
import { GUITAR_COUNT_SHOWN } from '../../consts'; //SortType, SortOrder,
import { Guitar } from '../../types/guitars';

function Catalog(): JSX.Element {

  const guitarsCatalog = useAppSelector(getCatalog);
  const priceLimits = useAppSelector(getPriceLimits);
  const guitarsFilterType:{[key: string]: boolean} = useAppSelector(getFilterTypes);
  const guitarsFilter4Strings = useAppSelector(getFilter4Strings);
  const guitarsFilter6Strings = useAppSelector(getFilter6Strings);
  const guitarsFilter7Strings = useAppSelector(getFilter7Strings);
  const guitarsFilter12Strings = useAppSelector(getFilter12Strings);

  const filterByPrice = (el:Guitar) => (el.price>=priceLimits.priceMin &&
    (priceLimits.priceMax!==0 ? el.price<=priceLimits.priceMax : el));

  const filterByType = (guitar: Guitar) => {
    if (guitarsFilterType.acoustic === false &&
      guitarsFilterType.electric === false &&
      guitarsFilterType.ukulele === false) {
      return guitar;
    }
    if (guitarsFilterType[guitar.type] === true ) {
      return guitar;
    }
  };

  const filterByCountStrings = (guitar: Guitar) => {
    if (guitarsFilter4Strings === false &&
      guitarsFilter6Strings === false &&
      guitarsFilter7Strings === false &&
      guitarsFilter12Strings === false) {
      return guitar;
    }

    if (guitarsFilter4Strings && guitar.stringCount===4) {
      return guitar;
    }
    if (guitarsFilter6Strings && guitar.stringCount===6) {
      return guitar;
    }
    if (guitarsFilter7Strings && guitar.stringCount===7) {
      return guitar;
    }
    if (guitarsFilter12Strings && guitar.stringCount===12) {
      return guitar;
    }
  };

  const guitarsCatalogFiltered = guitarsCatalog
    .filter(filterByPrice)
    .filter(filterByType)
    .filter(filterByCountStrings);

  // eslint-disable-next-line no-console
  console.log(guitarsCatalogFiltered);
  //const [searchParams, setSearchParams] = useSearchParams();

  // const [guitarsCatalogSorted, setGuitarsCatalogSorted] = useState(guitarsCatalog);
  // const [sortTypeChecked, setSortTypeChecked] = useState(SortType.Original);
  // const [sortOrderChecked, setSortOrderChecked] = useState(SortOrder.Original);

  const {pageNumber = 1} = useParams<{pageNumber: string}>();
  const pagesCount = Math.ceil(guitarsCatalogFiltered.length/GUITAR_COUNT_SHOWN);

  // const getChangeSortTypeChecked = () => {
  //   if (sortTypeChecked===SortType.Original) {
  //     setSortTypeChecked(SortType.Price);
  //   }
  // };

  // const getChangeSortOrderChecked = () => {
  //   if (sortOrderChecked===SortOrder.Original) {
  //     setSortOrderChecked(SortOrder.Up);
  //   }
  // };

  useEffect(() => {
    store.dispatch(fetchCatalogGuitars());
  }, []);

  // useEffect(() => {
  //   if (sortTypeChecked !== SortType.Original) {
  //     searchParams.set('sort', sortTypeChecked.toLowerCase());
  //     setSearchParams(searchParams);
  //   }
  //   if (sortOrderChecked !== SortOrder.Original) {
  //     searchParams.set('order', sortOrderChecked.toLowerCase());
  //     setSearchParams(searchParams);
  //   }

  //   if (sortTypeChecked !== SortType.Popularity) {
  //     if (sortOrderChecked === SortOrder.Up) {
  //       return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => prev.price - next.price));
  //     }

  //     if ( sortOrderChecked === SortOrder.Down) {
  //       return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => next.price - prev.price));
  //     }
  //   } else {
  //     if (sortOrderChecked === SortOrder.Up) {
  //       return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => prev.rating - next.rating));
  //     }

  //     if ( sortOrderChecked === SortOrder.Down) {
  //       return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => next.rating - prev.rating));
  //     }
  //   }
  //   return setGuitarsCatalogSorted(guitarsCatalog.slice(0));
  // }, [guitarsCatalog, sortTypeChecked, sortOrderChecked, searchParams, setSearchParams]);

  const guitarsForRender = guitarsCatalogFiltered.slice(GUITAR_COUNT_SHOWN*(Number(pageNumber)-1),
    GUITAR_COUNT_SHOWN*(Number(pageNumber)-1)+GUITAR_COUNT_SHOWN);

  return (
    <div className="catalog" data-testid="catalog">
      <Filter />
      <Sort />
      {/* <div className="catalog-sort">
        <h2 className="catalog-sort__title">Сортировать:</h2>
        <div className="catalog-sort__type">
          <button className={`catalog-sort__type-button
            ${sortTypeChecked===SortType.Price ? 'catalog-sort__type-button--active' : ''}`}
          aria-label="по цене"
          onClick={()=> {
            setSortTypeChecked(SortType.Price);
            getChangeSortOrderChecked();
          }}
          >по цене
          </button>
          <button className={`catalog-sort__type-button
          ${sortTypeChecked===SortType.Popularity ? 'catalog-sort__type-button--active' : ''}`}
          aria-label="по популярности"
          onClick={()=> {
            setSortTypeChecked(SortType.Popularity);
            getChangeSortOrderChecked();
          }}
          >по популярности
          </button>
        </div>
        <div className="catalog-sort__order">
          <button className={`catalog-sort__order-button catalog-sort__order-button--up
          ${sortOrderChecked===SortOrder.Up ? 'catalog-sort__order-button--active' : ''}`}
          aria-label="По возрастанию"
          onClick={()=> {
            setSortOrderChecked(SortOrder.Up);
            getChangeSortTypeChecked();
          }}
          >
          </button>
          <button className={`catalog-sort__order-button catalog-sort__order-button--down
          ${sortOrderChecked===SortOrder.Down ? 'catalog-sort__order-button--active' : ''}`}
          aria-label="По убыванию"
          onClick={()=> {
            setSortOrderChecked(SortOrder.Down);
            getChangeSortTypeChecked();
          }}
          >
          </button>
        </div>
      </div> */}

      <ProductList catalog={guitarsForRender} />
      <Pagination pagesCount={pagesCount} pageCurrent={Number(pageNumber)} />
    </div>
  );
}

export default Catalog;
