import { useSearchParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { getCatalog } from '../../store/guitars-data/selector';
import { useAppSelector } from '../../hooks';
import Preloader from '../preloader/preloader';
import { SortType, SortOrder } from '../../consts';

function Sort(): JSX.Element {
  const [searchParams, setSearchParams] = useSearchParams();
  const guitarsCatalog = useAppSelector(getCatalog);

  // const [guitarsCatalogSorted, setGuitarsCatalogSorted] = useState(guitarsCatalog);
  const [sortTypeChecked, setSortTypeChecked] = useState(SortType.Original);
  const [sortOrderChecked, setSortOrderChecked] = useState(SortOrder.Original);

  // eslint-disable-next-line no-console
  console.log(sortTypeChecked);
  // eslint-disable-next-line no-console
  console.log(sortOrderChecked);

  const getChangeSortTypeChecked = () => {
    if (sortTypeChecked===SortType.Original) {
      setSortTypeChecked(SortType.Price);
    }
  };

  const getChangeSortOrderChecked = () => {
    if (sortOrderChecked===SortOrder.Original) {
      setSortOrderChecked(SortOrder.Up);
    }
  };

  useEffect(() => {
    if (sortTypeChecked !== SortType.Original) {
      searchParams.set('sort', sortTypeChecked.toLowerCase());
      setSearchParams(searchParams);
    }
    if (sortOrderChecked !== SortOrder.Original) {
      searchParams.set('order', sortOrderChecked.toLowerCase());
      setSearchParams(searchParams);
    }

    // if (sortTypeChecked !== SortType.Popularity) {
    //   if (sortOrderChecked === SortOrder.Up) {
    //     return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => prev.price - next.price));
    //   }

    //   if ( sortOrderChecked === SortOrder.Down) {
    //     return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => next.price - prev.price));
    //   }
    // } else {
    //   if (sortOrderChecked === SortOrder.Up) {
    //     return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => prev.rating - next.rating));
    //   }

    //   if ( sortOrderChecked === SortOrder.Down) {
    //     return setGuitarsCatalogSorted(guitarsCatalog.slice(0).sort((prev, next) => next.rating - prev.rating));
    //   }
    // }
    // return setGuitarsCatalogSorted(guitarsCatalog.slice(0));
  }, [guitarsCatalog, sortTypeChecked, sortOrderChecked, searchParams, setSearchParams]);

  if (guitarsCatalog.length === 0) {
    return <Preloader />;
  }

  return (
    <div className="catalog-sort">
      <h2 className="catalog-sort__title">Сортировать:</h2>
      <div className="catalog-sort__type">
        <button className={`catalog-sort__type-button
            ${sortTypeChecked===SortType.Price ? 'catalog-sort__type-button--active' : ''}`}
        aria-label="по цене"
        onClick={()=> {
          setSortTypeChecked(SortType.Price);
          getChangeSortOrderChecked();
        }}
        >по цене
        </button>
        <button className={`catalog-sort__type-button
          ${sortTypeChecked===SortType.Popularity ? 'catalog-sort__type-button--active' : ''}`}
        aria-label="по популярности"
        onClick={()=> {
          setSortTypeChecked(SortType.Popularity);
          getChangeSortOrderChecked();
        }}
        >по популярности
        </button>
      </div>
      <div className="catalog-sort__order">
        <button className={`catalog-sort__order-button catalog-sort__order-button--up
          ${sortOrderChecked===SortOrder.Up ? 'catalog-sort__order-button--active' : ''}`}
        aria-label="По возрастанию"
        onClick={()=> {
          setSortOrderChecked(SortOrder.Up);
          getChangeSortTypeChecked();
        }}
        >
        </button>
        <button className={`catalog-sort__order-button catalog-sort__order-button--down
          ${sortOrderChecked===SortOrder.Down ? 'catalog-sort__order-button--active' : ''}`}
        aria-label="По убыванию"
        onClick={()=> {
          setSortOrderChecked(SortOrder.Down);
          getChangeSortTypeChecked();
        }}
        >
        </button>
      </div>
    </div>
  );
}

export default Sort;
