import { createSlice } from '@reduxjs/toolkit';
import { NameSpace } from '../../consts';
import { QuestsData } from '../../types/state';
import { Guitar, Guitars } from '../../types/guitars';
import { getSortByPrice } from '../../utils/utils';

export const initialState: QuestsData = {
  guitarsCatalog: [] as unknown as Guitars,
  guitarCurrent: {} as Guitar,
  guitarsComments: {} ,
  guitarsPriceRange: {
    priceMin: 0,
    priceMax: 0,
  },
};

export const guitarsData = createSlice({
  name: NameSpace.Data,
  initialState,
  reducers: {
    loadCatalog: (state, action) => {
      state.guitarsCatalog = action.payload;
    },
    loadGuitar: (state, action) => {
      state.guitarCurrent = action.payload;
    },
    loadComments: (state, action) => {
      state.guitarsComments = { ...state.guitarsComments, [action.payload.id]: action.payload.data};
    },
    loadCatalogPriceRange: (state, action) => {
      const catalogSortingByPrice = action.payload.slice(0).sort(getSortByPrice);

      state.guitarsPriceRange.priceMin = catalogSortingByPrice[0].price;
      state.guitarsPriceRange.priceMax = catalogSortingByPrice[catalogSortingByPrice.length-1].price;
    },
  },
});

export const { loadCatalog, loadGuitar, loadComments, loadCatalogPriceRange } = guitarsData.actions;
