import { useState, useEffect, ChangeEvent } from 'react';
import { useAppDispatch } from '../../hooks';
import { getCatalog, getMinPrice, getMaxPrice } from '../../store/guitars-data/selector';
import { updateFilterPriceLimit, updateFilterGuitarsType, updateFilterStringsCount } from '../../store/guitars-process/guitars-process';

import { useAppSelector } from '../../hooks';
import Preloader from '../preloader/preloader';

const guitarStrings = {
  'acoustic': ['6-strings', '7-strings', '12-strings'],
  'electric': ['4-strings', '6-strings', '7-strings'],
  'ukulele': ['4-strings'],
};

function Filter():JSX.Element {
  const dispatch = useAppDispatch();

  const guitarsCatalog = useAppSelector(getCatalog);
  const guitarsMinPrice = useAppSelector(getMinPrice);
  const guitarsMaxPrice = useAppSelector(getMaxPrice);

  const [filterPrice, setFilterMinPrice] = useState({
    priceMin: '',
    priceMax: '',
  });

  const [filterGuitarsType, setFilterGuitarsType] = useState({
    acoustic: false,
    electric: false,
    ukulele: false,
  });

  const [filterGuitarsStringsCount, setFilterGuitarsStringsCount] = useState({
    '4-strings': false,
    '6-strings': false,
    '7-strings': false,
    '12-strings': false,
  });

  // const [filterGuitars4Strings, setFilterGuitars4Strings ] = useState(false);
  // const [filterGuitars6Strings, setFilterGuitars6Strings ] = useState(false);
  // const [filterGuitars7Strings, setFilterGuitars7Strings ] = useState(false);
  // const [filterGuitars12Strings, setFilterGuitars12Strings ] = useState(false);

  useEffect(() => {
    const filterStringsItems = [''];

    if ( filterGuitarsType.acoustic ) {
      guitarStrings.acoustic.forEach((item) => {
        if (!filterStringsItems.includes(item)) {
          filterStringsItems.push(item);
        }
      });
    }

    if ( filterGuitarsType.electric ) {
      guitarStrings.electric.forEach((item) => {
        if (!filterStringsItems.includes(item)) {
          filterStringsItems.push(item);
        }
      });
    }

    if ( filterGuitarsType.ukulele ) {
      guitarStrings.ukulele.forEach((el) => {
        if (!filterStringsItems.includes(el)) {
          filterStringsItems.push(el);
        }
      });
    }

    if (filterStringsItems.length > 1) {
      setFilterGuitarsStringsCount({
        '4-strings': !filterStringsItems.includes('4-strings'),
        '6-strings': !filterStringsItems.includes('6-strings'),
        '7-strings': !filterStringsItems.includes('7-strings'),
        '12-strings': !filterStringsItems.includes('12-strings'),
      });
    } else {
      setFilterGuitarsStringsCount({
        '4-strings': false,
        '6-strings': false,
        '7-strings': false,
        '12-strings': false,
      });
    }
    dispatch(updateFilterPriceLimit(filterPrice));
    dispatch(updateFilterGuitarsType(filterGuitarsType));

  }, [dispatch, filterGuitarsType, filterPrice]);

  if (guitarsCatalog.length === 0) {
    return <Preloader />;
  }

  const fieldPriceChangeHandle = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;
    setFilterMinPrice({...filterPrice, [id]: value});
  };

  const handleMinPriceBlur = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;

    if ( value !== '' ) {
      if ( Number(value) < guitarsMinPrice ) {
        return setFilterMinPrice({...filterPrice, [id]: guitarsMinPrice});
      }

      if ( Number(value) > guitarsMaxPrice ) {
        return setFilterMinPrice({...filterPrice, [id]: guitarsMaxPrice});
      }

      if ( Number(value) > Number(filterPrice.priceMax) && filterPrice.priceMax !== '' ) {
        return setFilterMinPrice({...filterPrice, [id]: filterPrice.priceMax});
      }
      return setFilterMinPrice({...filterPrice, [id]: value});
    } else {
      return setFilterMinPrice({...filterPrice, [id]: ''});
    }
  };


  const handleMaxPriceBlur = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;

    if ( value !== '' ) {
      if ( Number(value) < guitarsMinPrice ) {
        return setFilterMinPrice({...filterPrice, [id]: guitarsMinPrice});
      }

      if ( Number(value) > guitarsMaxPrice ) {
        return setFilterMinPrice({...filterPrice, [id]: guitarsMaxPrice});
      }

      if ( Number(value) < Number(filterPrice.priceMin) && filterPrice.priceMin !== '' ) {
        return setFilterMinPrice({...filterPrice, [id]: filterPrice.priceMin});
      }
      return setFilterMinPrice({...filterPrice, [id]: value});
    } else {
      return setFilterMinPrice({...filterPrice, [id]: ''});
    }
  };

  const radioChangeHandler = (evt: ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = evt.target;

    setFilterGuitarsType({...filterGuitarsType, [name]: checked});
  };

  const onChangeStringCount = (evt: ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = evt.target;
    dispatch(updateFilterStringsCount({...filterGuitarsStringsCount, [name]: checked}));
  };

  const onResetClick = () => {
    setFilterMinPrice({
      priceMin: '',
      priceMax: '',
    });

    setFilterGuitarsType({
      acoustic: false,
      electric: false,
      ukulele: false,
    });

    setFilterGuitarsStringsCount({
      '4-strings': false,
      '6-strings': false,
      '7-strings': false,
      '12-strings': false,
    });
  };

  return (
    <form className="catalog-filter">
      <h2 className="title title--bigger catalog-filter__title">Фильтр</h2>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Цена, ₽</legend>
        <div className="catalog-filter__price-range">
          <div className="form-input">
            <label className="visually-hidden">Минимальная цена</label>
            <input type="number"  id="priceMin" name="от"
              placeholder={guitarsMinPrice.toLocaleString()}
              value={filterPrice.priceMin}
              onChange={fieldPriceChangeHandle}
              onBlur={handleMinPriceBlur}
            />
          </div>
          <div className="form-input">
            <label className="visually-hidden">Максимальная цена</label>
            <input type="number"   id="priceMax" name="до"
              placeholder={guitarsMaxPrice.toLocaleString()}
              value={filterPrice.priceMax}
              onChange={fieldPriceChangeHandle}
              onBlur={handleMaxPriceBlur}
            />
          </div>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Тип гитар</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="acoustic" name="acoustic"
            onChange={radioChangeHandler}
          />
          <label htmlFor="acoustic">Акустические гитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="electric" name="electric"
            onChange={radioChangeHandler}
          />
          <label htmlFor="electric">Электрогитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="ukulele" name="ukulele"
            onChange={radioChangeHandler}
          />
          <label htmlFor="ukulele">Укулеле</label>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Количество струн</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="4-strings" name="4-strings"
            // disabled={filterGuitars4Strings}
            disabled={filterGuitarsStringsCount['4-strings']}
            onChange={onChangeStringCount}
          />
          <label htmlFor="4-strings">4</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="6-strings" name="6-strings"
            // disabled={filterGuitars6Strings}
            disabled={filterGuitarsStringsCount['6-strings']}
            onChange={onChangeStringCount}
          />
          <label htmlFor="6-strings">6</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="7-strings" name="7-strings"
            // disabled={filterGuitars7Strings}
            disabled={filterGuitarsStringsCount['7-strings']}
            onChange={onChangeStringCount}
          />
          <label htmlFor="7-strings">7</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="12-strings" name="12-strings"
            // disabled={filterGuitars12Strings}
            disabled={filterGuitarsStringsCount['12-strings']}
            onChange={onChangeStringCount}
          />
          <label htmlFor="12-strings">12</label>
        </div>
      </fieldset>
      <button className="catalog-filter__reset-btn button button--black-border button--medium" type="reset"
        onClick={onResetClick}
      >Очистить
      </button>
    </form>
  );
}

export default Filter;
