import { useSearchParams } from 'react-router-dom';
import { useState, useEffect, ChangeEvent } from 'react'; //useRef
import { useAppDispatch, useAppSelector } from '../../hooks';
import { getMinPrice, getMaxPrice } from '../../store/guitars-data/selector';
import { updateFilterPrice, updateFilterTypes, updateFilter4Strings,
  updateFilter6Strings, updateFilter7Strings, updateFilter12Strings } from '../../store/guitars-process/guitars-process';


const guitarStrings :{[key: string]: string[]} = {
  'acoustic': ['6-strings', '7-strings', '12-strings'],
  'electric': ['4-strings', '6-strings', '7-strings'],
  'ukulele': ['4-strings'],
};

function Filter():JSX.Element {
  const dispatch = useAppDispatch();

  const [searchParams, setSearchParams] = useSearchParams();
  const guitarsMinPrice = useAppSelector(getMinPrice);
  const guitarsMaxPrice = useAppSelector(getMaxPrice);

  const [filterPrice, setFilterPrice] = useState({
    priceMin: '',
    priceMax: '',
  });


  const [filterType, setFilterType] = useState<{[key: string]: boolean} >({
    acoustic: false,
    electric: false,
    ukulele: false,
  });

  const [filter4StringsDisabled, setFilter4StringsDisabled] = useState(false);
  const [filter6StringsDisabled, setFilter6StringsDisabled] = useState(false);
  const [filter7StringsDisabled, setFilter7StringsDisabled] = useState(false);
  const [filter12StringsDisabled, setFilter12StringsDisabled] = useState(false);

  const [filter4StringsChecked, setFilter4StringsChecked] = useState(false);
  const [filter6StringsChecked, setFilter6StringsChecked] = useState(false);
  const [filter7StringsChecked, setFilter7StringsChecked] = useState(false);
  const [filter12StringsChecked, setFilter12StringsChecked] = useState(false);

  // const isMounted = useRef(true);

  useEffect(() => {

    // if (isMounted.current) {
    if(searchParams.has('priceMin')) {
      const priceMinRange = (searchParams.get('priceMin'));
      // setFilterPriceMin(priceMinRange as string);
      setFilterPrice({...filterPrice, 'priceMin': priceMinRange as string});
      // dispatch(updateFilterPriceMin(priceMinRange));
      dispatch(updateFilterPrice(priceMinRange));
    }

    if(searchParams.has('priceMax')) {
      const priceMaxRange = (searchParams.get('priceMax'));
      // setFilterPriceMax(priceMaxRange as string);
      setFilterPrice({...filterPrice, 'priceMax': priceMaxRange as string});
      // dispatch(updateFilterPriceMax(priceMaxRange));
      dispatch(updateFilterPrice(priceMaxRange));
    }
    // return () => {
    // isMounted.current = false;
    // };
    // }
  }, [dispatch, filterPrice, searchParams]);

  useEffect(() => {
    const filterStringsItems = [''];

    Object.keys(filterType).forEach((el)=>{
      if ( filterType[el] ) {
        guitarStrings[el].forEach((item) => {
          if (!filterStringsItems.includes(item)) {
            filterStringsItems.push(item);
          }
        });
      }
    });

    setFilter4StringsDisabled(filterStringsItems.length > 1 ? !filterStringsItems.includes('4-strings') : false);
    setFilter6StringsDisabled(filterStringsItems.length > 1 ? !filterStringsItems.includes('6-strings') : false);
    setFilter7StringsDisabled(filterStringsItems.length > 1 ? !filterStringsItems.includes('7-strings') : false);
    setFilter12StringsDisabled(filterStringsItems.length > 1 ? !filterStringsItems.includes('12-strings') : false);

    if (filterStringsItems.length > 1 && !filterStringsItems.includes('4-strings')) {
      setFilter4StringsChecked(false);
    }

    if (filterStringsItems.length > 1 && !filterStringsItems.includes('6-strings')) {
      setFilter6StringsChecked(false);
    }

    if (filterStringsItems.length > 1 && !filterStringsItems.includes('7-strings')) {
      setFilter7StringsChecked(false);
    }

    if (filterStringsItems.length > 1 && !filterStringsItems.includes('12-strings')) {
      setFilter12StringsChecked(false);
    }

    dispatch(updateFilterTypes(filterType));
    dispatch(updateFilter4Strings(filter4StringsChecked));
    dispatch(updateFilter6Strings(filter6StringsChecked));
    dispatch(updateFilter7Strings(filter7StringsChecked));
    dispatch(updateFilter12Strings(filter12StringsChecked));

  }, [dispatch, filterType, filter4StringsChecked, filter6StringsChecked, filter7StringsChecked, filter12StringsChecked]);

  const fieldPriceChangeHandle = (evt: ChangeEvent<HTMLInputElement>) => {
    const {id, value} = evt.target;
    // if (id === 'priceMin') {
    //   setFilterPriceMin(value);
    // } else {
    //   setFilterPriceMax(value);
    // }
    setFilterPrice({...filterPrice, [id]: value});
  };

  const handleMinPriceBlur = (evt: ChangeEvent<HTMLInputElement>) => {
    const {value} = evt.target;
    if ( Number(value) !== 0 ) {
      if ( Number(value) < guitarsMinPrice ) {
        // setFilterPriceMin(guitarsMinPrice.toString());
        setFilterPrice({...filterPrice, 'priceMin': guitarsMinPrice.toString()});
        searchParams.set('priceMin', guitarsMinPrice.toString());
        setSearchParams(searchParams);
        return;
      }

      if ( Number(value) > guitarsMaxPrice ) {
        // setFilterPriceMin(guitarsMaxPrice.toString());
        setFilterPrice({...filterPrice, 'priceMin': guitarsMaxPrice.toString()});
        searchParams.set('priceMin', guitarsMaxPrice.toString());
        setSearchParams(searchParams);
        return;
      }

      // if ( Number(value) > Number(filterPriceMax) && Number(filterPriceMax) !== 0) {
      if ( Number(value) > Number(filterPrice.priceMax) && Number(filterPrice.priceMax) !== 0) {
        // setFilterPriceMin(filterPriceMax);
        setFilterPrice({...filterPrice, 'priceMin': filterPrice.priceMax});
        // searchParams.set('priceMin', filterPriceMax.toString());
        searchParams.set('priceMin', filterPrice.priceMax);
        setSearchParams(searchParams);
        return;
      }
      // setFilterPriceMin(value);
      setFilterPrice({...filterPrice, 'priceMin': filterPrice.priceMax});
      searchParams.set('priceMin', value);
      setSearchParams(searchParams);
    } else {
      // setFilterPriceMin('0');
      setFilterPrice({...filterPrice, 'priceMin': '0'});
      searchParams.delete('priceMin');
      setSearchParams(searchParams);
    }
  };

  const handleMaxPriceBlur = (evt: ChangeEvent<HTMLInputElement>) => {
    const {value} = evt.target;

    if ( Number(value) !== 0 ) {
      if ( Number(value) < guitarsMinPrice ) {
        // setFilterPriceMax(guitarsMinPrice.toString());
        setFilterPrice({...filterPrice, 'priceMax': guitarsMinPrice.toString()});
        searchParams.set('priceMax', guitarsMinPrice.toString());
        setSearchParams(searchParams);
        return;
      }

      if ( Number(value) > guitarsMaxPrice ) {
        // setFilterPriceMax(guitarsMaxPrice.toString());
        setFilterPrice({...filterPrice, 'priceMax': guitarsMaxPrice.toString()});
        searchParams.set('priceMax', guitarsMaxPrice.toString());
        setSearchParams(searchParams);
        return;
      }

      // if ( Number(value) < Number(filterPriceMin) && Number(filterPriceMin) !== 0) {
      if ( Number(value) < Number(filterPrice.priceMin) && Number(filterPrice.priceMin) !== 0) {
        // setFilterPriceMax(filterPriceMin);
        setFilterPrice({...filterPrice, 'priceMax': filterPrice.priceMin});
        searchParams.set('priceMax', filterPrice.priceMin);
        setSearchParams(searchParams);
        return;
      }
      // setFilterPriceMax(value);
      setFilterPrice({...filterPrice, 'priceMax': value.toString()});
      searchParams.set('priceMax', value.toString());
      setSearchParams(searchParams);
    } else {
      // setFilterPriceMax('0');
      setFilterPrice({...filterPrice, 'priceMax': '0'});
      searchParams.delete('priceMax');
      setSearchParams(searchParams);
    }
  };

  const radioChangeHandler = (evt: ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = evt.target;
    setFilterType({...filterType, [name]: checked});
    if (checked) {
      // searchParams.set('filter_type', name);
      // setSearchParams(searchParams);
      // return;
    }
    // searchParams.delete('filter_type');
    // setSearchParams(searchParams);
  };

  const onResetClick = () => {
    setFilterPrice({
      priceMin: '0',
      priceMax: '0',
    });

    setFilterType({
      acoustic: false,
      electric: false,
      ukulele: false,
    });

    setFilter4StringsDisabled(false);
    setFilter6StringsDisabled(false);
    setFilter7StringsDisabled(false);
    setFilter12StringsDisabled(false);

    // searchParams.delete('filter_priceMin');
    // searchParams.delete('filter_priceMax');
    // setSearchParams(searchParams);
  };

  return (
    <form className="catalog-filter">
      <h2 className="title title--bigger catalog-filter__title">Фильтр</h2>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Цена, ₽</legend>
        <div className="catalog-filter__price-range">
          <div className="form-input">
            <label className="visually-hidden">Минимальная цена</label>
            <input type="number"  id="priceMin" name="от"
              // value={Number(filterPriceMin)===0 ? '' : filterPriceMin}
              // value={filterPriceMin}
              value={filterPrice.priceMin}
              placeholder={guitarsMinPrice.toLocaleString()}
              onChange={fieldPriceChangeHandle}
              onBlur={handleMinPriceBlur}
            />
          </div>
          <div className="form-input">
            <label className="visually-hidden">Максимальная цена</label>
            <input type="number"   id="priceMax" name="до"
              // value={Number(filterPriceMax)===0 ? '' : filterPriceMax}
              // value={filterPriceMax}
              value={filterPrice.priceMax}
              placeholder={guitarsMaxPrice.toLocaleString()}
              onChange={fieldPriceChangeHandle}
              onBlur={handleMaxPriceBlur}
            />
          </div>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Тип гитар</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="acoustic" name="acoustic"
            onChange={radioChangeHandler}
          />
          <label htmlFor="acoustic">Акустические гитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="electric" name="electric"
            onChange={radioChangeHandler}
          />
          <label htmlFor="electric">Электрогитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="ukulele" name="ukulele"
            onChange={radioChangeHandler}
          />
          <label htmlFor="ukulele">Укулеле</label>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Количество струн</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="4-strings" name="4-strings"
            disabled={filter4StringsDisabled}
            checked={filter4StringsChecked}
            onChange={(evt)=>setFilter4StringsChecked(evt.target.checked)}
          />
          <label htmlFor="4-strings">4</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="6-strings" name="6-strings"
            disabled={filter6StringsDisabled}
            checked={filter6StringsChecked}
            onChange={(evt)=>setFilter6StringsChecked(evt.target.checked)}
          />
          <label htmlFor="6-strings">6</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="7-strings" name="7-strings"
            disabled={filter7StringsDisabled}
            checked={filter7StringsChecked}
            onChange={(evt)=>setFilter7StringsChecked(evt.target.checked)}
          />
          <label htmlFor="7-strings">7</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="12-strings" name="12-strings"
            disabled={filter12StringsDisabled}
            checked={filter12StringsChecked}
            onChange={(evt)=>setFilter12StringsChecked(evt.target.checked)}
          />
          <label htmlFor="12-strings">12</label>
        </div>
      </fieldset>
      <button className="catalog-filter__reset-btn button button--black-border button--medium" type="reset"
        onClick={onResetClick}
      >Очистить
      </button>
    </form>
  );
}

export default Filter;
