import { createSlice } from '@reduxjs/toolkit';
import { NameSpace } from '../../consts';
import { QuestsProcess } from '../../types/state';

export const initialState: QuestsProcess = {
  pageCurrent: '1',
  filterPrice:{
    priceMin: 0,
    priceMax: 0,
  },
  filterGuitarsType: {
    acoustic: false,
    electric: false,
    ukulele: false,
  },
};

export const guitarsProcess = createSlice({
  name: NameSpace.Process,
  initialState,
  reducers: {
    choosePage: (state, action) => {
      state.pageCurrent = action.payload;
    },
    updateFilterPriceLimit: (state, action) => {
      state.filterPrice = action.payload;
    },
    updateFilterGuitarsType: (state, action) => {
      state.filterGuitarsType = action.payload;
      // eslint-disable-next-line no-console
      console.log(state.filterGuitarsType);
    },
    updateFilterCount
  },
});

export const { choosePage, updateFilterPriceLimit, updateFilterGuitarsType } = guitarsProcess.actions;
