import { useRef, ChangeEvent, useState } from 'react';
import { getCatalog } from '../../store/guitars-data/selector';
import { useAppSelector } from '../../hooks';
import Preloader from '../preloader/preloader';
// import {Guitar} from '../../types/guitars';

function Filter():JSX.Element {
  const guitarsCatalog = useAppSelector(getCatalog);

  const catalogSortingByPrice = guitarsCatalog.slice(0).sort((prev, next) => prev.price - next.price);
  const priceMin = useRef(0);
  const priceMax = useRef(0);

  const [filterPriceParameters, setFilterPriceParameters ] = useState({
    priceMin: priceMin.current,
    priceMax: priceMax.current,
  });

  if (catalogSortingByPrice.length === 0) {
    return <Preloader />;
  }

  if ( catalogSortingByPrice.length>0 ) {
    priceMin.current = catalogSortingByPrice[0].price;
    priceMax.current = catalogSortingByPrice[guitarsCatalog.length-1].price;
  }


  // eslint-disable-next-line no-console
  // console.log(catalogSortingByPrice);

  // eslint-disable-next-line no-console
  // console.log(`длина массива=>${catalogSortingByPrice.length}`);


  // eslint-disable-next-line no-console
  // console.log(`priceMin=>${priceMin.current}`);
  // eslint-disable-next-line no-console
  // console.log(`priceMax=>${priceMax.current}`);

  const fieldChangeHandle = (evt: ChangeEvent<HTMLInputElement>) => {
    const { id, value } = evt.target; //
    // eslint-disable-next-line no-console
    console.log(value);
    // eslint-disable-next-line no-console
    console.log(priceMin.current);
    if (Number(value) < 0) {
      const valuePrice = Math.abs(Number(value));
      // eslint-disable-next-line no-console
      console.log(valuePrice);
      setFilterPriceParameters({...filterPriceParameters, [id]: valuePrice});
    }
  };

  return (
    <form className="catalog-filter">
      <h2 className="title title--bigger catalog-filter__title">Фильтр</h2>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Цена, ₽</legend>
        <div className="catalog-filter__price-range">
          <div className="form-input">
            <label className="visually-hidden">Минимальная цена</label>
            <input type="number"  id="priceMin" name="от"
              placeholder={priceMin.current.toLocaleString()}
              min={priceMin.current}
              max={priceMax.current}
              // value={filterPriceParameters.priceMin}
              onChange={fieldChangeHandle}
            />
          </div>
          <div className="form-input">
            <label className="visually-hidden">Максимальная цена</label>
            <input type="number"   id="priceMax" name="до"
              placeholder={priceMax.current.toLocaleString()}
              min={priceMin.current}
              max={priceMax.current}
              // value={filterPriceParameters.priceMax}
            />
          </div>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Тип гитар</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="acoustic" name="acoustic"/>
          <label htmlFor="acoustic">Акустические гитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="electric" name="electric"/>
          <label htmlFor="electric">Электрогитары</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="ukulele" name="ukulele"/>
          <label htmlFor="ukulele">Укулеле</label>
        </div>
      </fieldset>
      <fieldset className="catalog-filter__block">
        <legend className="catalog-filter__block-title">Количество струн</legend>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="4-strings" name="4-strings"/>
          <label htmlFor="4-strings">4</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="6-strings" name="6-strings"/>
          <label htmlFor="6-strings">6</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="7-strings" name="7-strings"/>
          <label htmlFor="7-strings">7</label>
        </div>
        <div className="form-checkbox catalog-filter__block-item">
          <input className="visually-hidden" type="checkbox" id="12-strings" name="12-strings"/>
          <label htmlFor="12-strings">12</label>
        </div>
      </fieldset>
      <button className="catalog-filter__reset-btn button button--black-border button--medium" type="reset">Очистить</button>
    </form>
  );
}

export default Filter;
