import { createSlice } from '@reduxjs/toolkit';
import { NameSpace } from '../../consts';
import { QuestsProcess } from '../../types/state';

export const initialState: QuestsProcess = {
  pageCurrent: '1',
  filterPrice:{
    priceMin: 0,
    priceMax: 0,
  },
  filterType: {
    acoustic: false,
    electric: false,
    ukulele: false,
  },
  filterStringsCount: {
    '4-strings': false,
    '6-strings': false,
    '7-strings': false,
    '12-strings': false,
  },
};

export const guitarsProcess = createSlice({
  name: NameSpace.Process,
  initialState,
  reducers: {
    choosePage: (state, action) => {
      state.pageCurrent = action.payload;
    },
    updateFilterPriceLimit: (state, action) => {
      state.filterPrice = action.payload;
    },
    updateFilterTypes: (state, action) => {
      state.filterTypes = action.payload;
    },
    updateFilterStringsCount : (state, action) => {
      state.filterStringsCount = action.payload;
      // eslint-disable-next-line no-console
      // console.log('state.filterGuitarsStringsCount');
      // eslint-disable-next-line no-console
      // console.log(state.filterGuitarsStringsCount);
    },
  },
});

export const { choosePage, updateFilterPriceLimit, updateFilterTypes, updateFilterStringsCount } = guitarsProcess.actions;
